const barryImage = "data:image/jpeg;base64,";
const clioImage = 'data:image/png;base64,';

const defaultImage = "data:image/jpeg;base64,"

class ImageHelper {
  static getImage(name: string) {
    name = name.toLocaleLowerCase();
    if (name.includes("barry")) {
      return barryImage;
    } else if(name.includes("clio")) {
      return clioImage;
    }
    return defaultImage;
  }
}

export default ImageHelper;